int parse_options(struct futhark_context_config *cfg, int argc,
                  char *const argv[])
{
    int ch;
    static struct option long_options[] = {{"write-runtime-to",
                                            required_argument, NULL, 1},
                                           {"runs", required_argument, NULL, 2},
                                           {"debugging", no_argument, NULL, 3},
                                           {"log", no_argument, NULL, 4},
                                           {"entry-point", required_argument,
                                            NULL, 5}, {"binary-output",
                                                       no_argument, NULL, 6},
                                           {"no-print-result", no_argument,
                                            NULL, 7}, {"help", no_argument,
                                                       NULL, 8},
                                           {"print-params", no_argument, NULL,
                                            9}, {"param", required_argument,
                                                 NULL, 10}, {"tuning",
                                                             required_argument,
                                                             NULL, 11},
                                           {"device", required_argument, NULL,
                                            12}, {"default-group-size",
                                                  required_argument, NULL, 13},
                                           {"default-num-groups",
                                            required_argument, NULL, 14},
                                           {"default-tile-size",
                                            required_argument, NULL, 15},
                                           {"default-reg-tile-size",
                                            required_argument, NULL, 16},
                                           {"default-threshold",
                                            required_argument, NULL, 17},
                                           {"platform", required_argument, NULL,
                                            18}, {"dump-opencl",
                                                  required_argument, NULL, 19},
                                           {"load-opencl", required_argument,
                                            NULL, 20}, {"dump-opencl-binary",
                                                        required_argument, NULL,
                                                        21},
                                           {"load-opencl-binary",
                                            required_argument, NULL, 22},
                                           {"build-option", required_argument,
                                            NULL, 23}, {"profile", no_argument,
                                                        NULL, 24},
                                           {"list-devices", no_argument, NULL,
                                            25}, {0, 0, 0, 0}};
    static char *option_descriptions =
                "  -t/--write-runtime-to FILE  Print the time taken to execute the program to the indicated file, an integral number of microseconds.\n  -r/--runs INT               Perform NUM runs of the program.\n  -D/--debugging              Perform possibly expensive internal correctness checks and verbose logging.\n  -L/--log                    Print various low-overhead logging information to stderr while running.\n  -e/--entry-point NAME       The entry point to run. Defaults to main.\n  -b/--binary-output          Print the program result in the binary output format.\n  -n/--no-print-result        Do not print the program result.\n  -h/--help                   Print help information and exit.\n  --print-params              Print all tuning parameters that can be set with --param or --tuning.\n  --param ASSIGNMENT          Set a tuning parameter to the given value.\n  --tuning FILE               Read size=value assignments from the given file.\n  -d/--device NAME            Use the first OpenCL device whose name contains the given string.\n  --default-group-size INT    The default size of OpenCL workgroups that are launched.\n  --default-num-groups INT    The default number of OpenCL workgroups that are launched.\n  --default-tile-size INT     The default tile size used when performing two-dimensional tiling.\n  --default-reg-tile-size INT The default register tile size used when performing two-dimensional tiling.\n  --default-threshold INT     The default parallelism threshold.\n  -p/--platform NAME          Use the first OpenCL platform whose name contains the given string.\n  --dump-opencl FILE          Dump the embedded OpenCL program to the indicated file.\n  --load-opencl FILE          Instead of using the embedded OpenCL program, load it from the indicated file.\n  --dump-opencl-binary FILE   Dump the compiled version of the embedded OpenCL program to the indicated file.\n  --load-opencl-binary FILE   Load an OpenCL binary from the indicated file.\n  --build-option OPT          Add an additional build option to the string passed to clBuildProgram().\n  -P/--profile                Gather profiling data while executing and print out a summary at the end.\n  --list-devices              List all OpenCL devices and platforms available on the system.\n";
    
    while ((ch = getopt_long(argc, argv, ":t:r:DLe:bnhd:p:P", long_options,
                             NULL)) != -1) {
        if (ch == 1 || ch == 't') {
            runtime_file = fopen(optarg, "w");
            if (runtime_file == NULL)
                futhark_panic(1, "Cannot open %s: %s\n", optarg,
                              strerror(errno));
        }
        if (ch == 2 || ch == 'r') {
            num_runs = atoi(optarg);
            perform_warmup = 1;
            if (num_runs <= 0)
                futhark_panic(1, "Need a positive number of runs, not %s\n",
                              optarg);
        }
        if (ch == 3 || ch == 'D')
            futhark_context_config_set_debugging(cfg, 1);
        if (ch == 4 || ch == 'L')
            futhark_context_config_set_logging(cfg, 1);
        if (ch == 5 || ch == 'e') {
            if (entry_point != NULL)
                entry_point = optarg;
        }
        if (ch == 6 || ch == 'b')
            binary_output = 1;
        if (ch == 7 || ch == 'n')
            print_result = 0;
        if (ch == 8 || ch == 'h') {
            printf("Usage: %s [OPTION]...\nOptions:\n\n%s\nFor more information, consult the Futhark User's Guide or the man pages.\n",
                   fut_progname, option_descriptions);
            exit(0);
        }
        if (ch == 9) {
            int n = futhark_get_tuning_param_count();
            
            for (int i = 0; i < n; i++)
                printf("%s (%s)\n", futhark_get_tuning_param_name(i),
                       futhark_get_tuning_param_class(i));
            exit(0);
        }
        if (ch == 10) {
            char *name = optarg;
            char *equals = strstr(optarg, "=");
            char *value_str = equals != NULL ? equals + 1 : optarg;
            int value = atoi(value_str);
            
            if (equals != NULL) {
                *equals = 0;
                if (futhark_context_config_set_tuning_param(cfg, name,
                                                            (size_t) value) !=
                    0)
                    futhark_panic(1, "Unknown size: %s\n", name);
            } else
                futhark_panic(1, "Invalid argument for size option: %s\n",
                              optarg);
        }
        if (ch == 11) {
            char *ret = load_tuning_file(optarg, cfg, (int (*)(void *, const
                                                               char *,
                                                               size_t)) futhark_context_config_set_tuning_param);
            
            if (ret != NULL)
                futhark_panic(1, "When loading tuning from '%s': %s\n", optarg,
                              ret);
        }
        if (ch == 12 || ch == 'd')
            futhark_context_config_set_device(cfg, optarg);
        if (ch == 13)
            futhark_context_config_set_default_group_size(cfg, atoi(optarg));
        if (ch == 14)
            futhark_context_config_set_default_num_groups(cfg, atoi(optarg));
        if (ch == 15)
            futhark_context_config_set_default_tile_size(cfg, atoi(optarg));
        if (ch == 16)
            futhark_context_config_set_default_reg_tile_size(cfg, atoi(optarg));
        if (ch == 17)
            futhark_context_config_set_default_threshold(cfg, atoi(optarg));
        if (ch == 18 || ch == 'p')
            futhark_context_config_set_platform(cfg, optarg);
        if (ch == 19) {
            futhark_context_config_dump_program_to(cfg, optarg);
            entry_point = NULL;
        }
        if (ch == 20)
            futhark_context_config_load_program_from(cfg, optarg);
        if (ch == 21) {
            futhark_context_config_dump_binary_to(cfg, optarg);
            entry_point = NULL;
        }
        if (ch == 22)
            futhark_context_config_load_binary_from(cfg, optarg);
        if (ch == 23)
            futhark_context_config_add_build_option(cfg, optarg);
        if (ch == 24 || ch == 'P')
            futhark_context_config_set_profiling(cfg, 1);
        if (ch == 25) {
            futhark_context_config_list_devices(cfg);
            entry_point = NULL;
        }
        if (ch == ':')
            futhark_panic(-1, "Missing argument for option %s\n", argv[optind -
                                                                       1]);
        if (ch == '?') {
            fprintf(stderr, "Usage: %s [OPTIONS]...\nOptions:\n\n%s\n",
                    fut_progname,
                    "  -t/--write-runtime-to FILE  Print the time taken to execute the program to the indicated file, an integral number of microseconds.\n  -r/--runs INT               Perform NUM runs of the program.\n  -D/--debugging              Perform possibly expensive internal correctness checks and verbose logging.\n  -L/--log                    Print various low-overhead logging information to stderr while running.\n  -e/--entry-point NAME       The entry point to run. Defaults to main.\n  -b/--binary-output          Print the program result in the binary output format.\n  -n/--no-print-result        Do not print the program result.\n  -h/--help                   Print help information and exit.\n  --print-params              Print all tuning parameters that can be set with --param or --tuning.\n  --param ASSIGNMENT          Set a tuning parameter to the given value.\n  --tuning FILE               Read size=value assignments from the given file.\n  -d/--device NAME            Use the first OpenCL device whose name contains the given string.\n  --default-group-size INT    The default size of OpenCL workgroups that are launched.\n  --default-num-groups INT    The default number of OpenCL workgroups that are launched.\n  --default-tile-size INT     The default tile size used when performing two-dimensional tiling.\n  --default-reg-tile-size INT The default register tile size used when performing two-dimensional tiling.\n  --default-threshold INT     The default parallelism threshold.\n  -p/--platform NAME          Use the first OpenCL platform whose name contains the given string.\n  --dump-opencl FILE          Dump the embedded OpenCL program to the indicated file.\n  --load-opencl FILE          Instead of using the embedded OpenCL program, load it from the indicated file.\n  --dump-opencl-binary FILE   Dump the compiled version of the embedded OpenCL program to the indicated file.\n  --load-opencl-binary FILE   Load an OpenCL binary from the indicated file.\n  --build-option OPT          Add an additional build option to the string passed to clBuildProgram().\n  -P/--profile                Gather profiling data while executing and print out a summary at the end.\n  --list-devices              List all OpenCL devices and platforms available on the system.\n");
            futhark_panic(1, "Unknown option: %s\n", argv[optind - 1]);
        }
    }
    return optind;
}
