    OPENCL_SUCCEED_FATAL(clReleaseKernel(ctx->builtinzhreplicate_i32zireplicate_17080));
    OPENCL_SUCCEED_FATAL(clReleaseKernel(ctx->mainzisegmap_16204));
    OPENCL_SUCCEED_FATAL(clReleaseKernel(ctx->mainzisegmap_16242));
    OPENCL_SUCCEED_FATAL(clReleaseKernel(ctx->mainzisegmap_16297));
    OPENCL_SUCCEED_FATAL(clReleaseKernel(ctx->mainzisegred_large_16275));
    OPENCL_SUCCEED_FATAL(clReleaseKernel(ctx->mainzisegred_small_16275));
    OPENCL_SUCCEED_FATAL(clReleaseMemObject(ctx->global_failure));
